/* eslint-disable no-unused-vars */
/* eslint-disable jsx-a11y/label-has-associated-control */
import React from 'react';
import { BiImageAdd } from 'react-icons/bi';
import { Divider } from 'rsuite';
import Class from '../assets/css/inputs.file.module.css';

export default function File({ title, name, handler, width }) {
    return (
        <div className={Class.container} style={{ width: `${width}` }}>
            <div className={Class.profile}>
                <input
                    type="file"
                    id={`${name}_file`}
                    onChange={(event) => handler(name, event.target.files[0])}
                />
                <label htmlFor={`${name}_file`} className="m-0 p-0">
                    <BiImageAdd className="text-white" />
                    <Divider vertical />
                    <span className="text-white">{title}</span>
                </label>
            </div>
        </div>
    );
}
