import { configureStore } from '@reduxjs/toolkit';
import DeviceagentSlice from '../features/DeviceagentSlice';

const Store = configureStore({
    reducer: {
        device: DeviceagentSlice,
    },
    middleware: (getDefaultMiddleware) =>
        getDefaultMiddleware({
            serializableCheck: {
                ignoredActions: [
                    'category/getallCategories/fulfilled',
                    'category/getallPCategories/fulfilled',
                    'category/getallGCategories/fulfilled',
                    'category/getallDpCategories/fulfilled',
                    'featured/categories/fulfilled',
                    'featured/brands/fulfilled',
                    'featured/products/fulfilled',
                    'featured/courses/fulfilled',
                    'featured/instructors/fulfilled',
                    'campaign/categoryCampaign/fulfilled',
                    'campaign/brandCampaign/fulfilled',
                    'campaign/productCampaign/fulfilled',
                    'product/getAllProducts/fulfilled',
                    'product/getNextPage/fulfilled',
                    'product/getDetails/fulfilled',
                    'course/getAllCourses/fulfilled',
                    'course/getCourseDetails/fulfilled',
                    'course/getAllInstructor/fulfilled',
                    'post/getAllPosts/fulfilled',
                    'post/getNextPage/fulfilled',
                    'post/viewCounter/fulfilled',
                    'auth/auth/fulfilled',
                    'auth/authConfirm/fulfilled',
                    'auth/userInfo/fulfilled',
                ],
            },
        }),
});
export default Store;
