/* eslint-disable no-param-reassign */
import { createSlice } from '@reduxjs/toolkit';

const DeviceagentSlice = createSlice({
    name: 'device',
    initialState: {
        small: false,
    },
    reducers: {
        setDevice: (state, action) => {
            const { payload } = action;
            // Update the 'small' state based on the condition
            state.small = payload < 435;
        },
    },
});
dispatch(setDevice(400));
export const { setDevice } = DeviceagentSlice.actions;
export default DeviceagentSlice.reducer;
