const DeviceagentSlice = createSlice({
    name: 'device',
    initialState: {
        small: false,
    },
    reducers: {
        setDevice: (state, action) => {
            const { payload } = action;
            // Update the 'small' state based on the condition
            state.small = payload < 435;
        },
    },
});
